	(241, 'Saftey', NULL, 'beginner', 0, 'Grab the right ski under the boot with your right hand, while the skis are uncrossed.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:04'),
	(242, 'Japan', NULL, 'beginner', 0, 'Put your right hand under the right knee and grab the left ski under the boot, uncrossed. Lift your knees and reach to grab the outer edge.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:04'),
	(243, 'Tail', NULL, 'beginner', 0, 'Grab the right tail with your right hand, skis crossed. Left ski should be over the right one.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:04'),
	(244, 'Mute', NULL, 'beginner', 0, 'Grab the left ski near the binding with your right hand while skis are crossed.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:04'),
	(245, 'Blunt grab', NULL, 'intermediate', 0, 'You grab the outside edge of the right ski with your right hand when the skis are uncrossed.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:48'),
	(246, 'Shifty', NULL, 'intermediate', 0, 'Shifty is when your upper body and the skis twist in different directions while in the air.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:48'),
	(247, 'Critical', NULL, 'intermediate', 0, 'Grab the the inside edge of the ski closer to the tip with your opposite hand. Knees are tucked and skis uncrossed.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:48'),
	(248, 'High Mute', NULL, 'intermediate', 0, 'Grab a mute, then slide your hand further from the boot.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:48'),
	(249, 'Nose', NULL, 'intermediate', 0, 'Nose means grabbing the front tip of your ski. Your leg is fully extended.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:51:48'),
	(250, 'Stail Fish', NULL, 'expert', 0, 'Grab the tail of your left ski with your right hand. Legs are turned sideways.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:46'),
	(251, 'Double Mute', NULL, 'expert', 0, 'Grab a high mute and slide your hand far enough to grab the ski with another hand or do a high mute with your right hand and then grab a nose with the left.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:46'),
	(252, 'Double Japan', NULL, 'expert', 0, 'Grab japan with the right hand while gripping the same ski inside with the left one. Right hand should be behind the right knee and the left one in front.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:46'),
	(253, 'Truck Driver', NULL, 'expert', 0, 'Truck Driver is a double nose grab — you have to extend your both legs and grip the front tips of your skis.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:46'),
	(254, 'Guitar', NULL, 'expert', 0, 'Grab the front tip with your left hand and pull it closer to cross the skis. Then reach under the left ski and grab it with your right hand higher then the left one.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:46'),
	(255, 'Bow Arrow', NULL, 'extreme', 0, 'Grab a truckdriver and, while keeping it, bend your left knee and pull the leg closer to you. It looks and feels like drawing a bow, hence the name.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:59'),
	(256, 'Octo Grab', NULL, 'extreme', 0, 'A double grab where your right hand grips the tail of the right ski and your left hand grips the front tip of the left. Slide your both hands like doing a high mute.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:59'),
	(257, 'Double Tail', NULL, 'extreme', 0, 'Either grab a tail and push it with your right hand in front, then grab it with your left hand, or grab both tails with your hands.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:59'),
	(258, 'Nose Japan', NULL, 'extreme', 0, 'Grab japan with your right hand, grip the nose of the same ski with the left one.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:59'),
	(259, 'Seatbelt', NULL, 'extreme', 0, 'A hand reaches across the body and grabs opposite ski Japan, while spinning.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:59'),
	(260, 'Taipan', NULL, 'extreme', 0, 'Hand reaching behind the leg on same side and grabbing opposite ski Mute, while spinning. Can be done crossed or uncrossed.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:59'),
	(261, 'Venom Grab', NULL, 'extreme', 0, 'Skis crossed, both hands grabbing Mute simultaneously.', 'trick', NULL, 'ski', 'grab', '2023-08-08 12:53:59'),
